<script type="text/javascript">
function getTargetFolder()
{
  var selection = $("#targetfolder");
  var value = selection.val();
  if (value == null || value == "")
  {
    value = ".";
  }
  var len = value.length;
  if (value.charAt(len - 1) == "\\" || value.charAt(len - 1) == "/")
  {
    value = value.substring(0, len - 1);
  }
  return value;
}

function getSelectedTableName()
{
  var selection = $("#tablename");
  var selectedOption = selection.find("option:selected");
  var value = null;
  if (selectedOption.length > 0)
  {
    value = selectedOption.get(0).value;
  }
  if (value != null && value == "")
  {
    value = null;
  }
  return value;
}

function handleTableNameChange()
{
  var tableName = getSelectedTableName();
  if (tableName != null)
  {
    document.location.replace("m13e.php?tablename=" + encodeURIComponent(tableName));
  } else
  {
    editor.setValue("");
    refreshLabels();
    editor.setOption("readOnly", "nocursor");
    $("#saveGenerate").prop("disabled", true);
    $("#regenerateXML").prop("disabled", true);
  }
}

function loadDefinition()
{
  $('#wait').fadeIn().css('display', 'table');
  var tableName = getSelectedTableName();

  var formData = new FormData();
  formData.append("action", "LOAD");
  formData.append("tablename", tableName);
  formData.append("XDEBUG_SESSION_START", "netbeans-xdebug");
  $.ajax({
    url: "m13e.php",
    data: formData,
    cache: false,
    contentType: false,
    processData: false,
    method: 'POST',
    type: 'POST', // For jQuery before 1.9
    async: true,
    cache: false,
            headers: {"cache-control": "no-cache"},
    success: function (_data, _textStatus, _jqXHR)
    {
      $('#wait').fadeOut(400, function () {
        $('#wait').css('display', 'none');
      });
      if (_data != null && _data.indexOf("<" + "?xml") == 0)
      {
        editor.setValue(_data);
        refreshLabels();
      } else
      {
        showMessages(_data);
      }
    },
    error: function (_jqXHR, _textStatus, _errorThrown)
    {
      $('#wait').fadeOut(400, function () {
        $('#wait').css('display', 'none');
      });
      showMessages("Communication error: " + _textStatus);
    }
  });
}

