function getParserError(e)
{
  var r = null;
  if(e.localName=="parsererror")
  {
    r = e;
  }
  else
  {
    var children = e.children;
    if(children!=null)
    {
      var len = children.length;
      var i = 0;
      while(r==null && i<len)
      {
        r = getParserError(children[i]);
        i++;
      }
    }
  }
  return r;
}

function getLayout()
{
  var r = null;
  var xmlData = editor.getValue();
  var parser = new DOMParser();
  var doc = parser.parseFromString(xmlData, "application/xml");
  var parserError = getParserError(doc.documentElement);
  if(parserError==null)
  {
    var root = doc.documentElement;
    r = root.getAttribute("layout");
    if(r==null || r=="")
    {
      r = "split";
    }
  }
  return r;
}

function getTableColumns(doc)
{
  var r = [];
  var root = doc.documentElement;
  var tableElements = root.getElementsByTagName("table");
  if(tableElements!=null && tableElements.length>0)
  {
    var tableElement = tableElements[0];
    var columnElements = tableElement.getElementsByTagName("column");
    if(columnElements!=null && columnElements.length>0)
    {
      var len = columnElements.length;
      for(var i=0; i<len; i++)
      {
        var columnElement = columnElements[i];
        var c = {};
        c.name = columnElement.getAttribute("name");
        c.label = columnElement.getAttribute("label");
        r.push(c);
      }
    }
  }
  return r;
}

