function refreshLabels()
{
  var xmlData = editor.getValue();
  var parser = new DOMParser();
  var doc = parser.parseFromString(xmlData, "application/xml");
  var parserError = getParserError(doc.documentElement);
  if(parserError==null)
  {
    $("#labelsContainer").html("");
    clearMessages();

    var columns = getTableColumns(doc);
    if(columns!=null && columns.length>0)
    {
      var table = document.createElement("table");
      table.className = "labelEditorTable";
      var len = columns.length;
      var firstInput = null;
      for(var i=0; i<len; i++)
      {
        var c = columns[i];
        var tr = document.createElement("tr");
        var td = document.createElement("td");
        var label = document.createElement("label");
        label.innerHTML = c.name + ":";
        label.htmlFor = "columnlabel_" + String(i);
        label.className = "labelEditorLabel";
        td.appendChild(label);
        tr.appendChild(td);
        td = document.createElement("td");
        var input = document.createElement("input");
        input.id = "columnlabel_" + String(i);
        input.type = "text";
        input.value = c.label!=null ? c.label : "";
        input.className = "labelEditorInput";
        input.spellcheck = false;
        td.appendChild(input);
        tr.appendChild(td);
        table.appendChild(tr);
        if(i==0)
        {
          firstInput = input;
        }
      }
      var labelsContainerDiv = document.getElementById("labelsContainer");
      labelsContainerDiv.appendChild(table);
      firstInput.focus();
    }
    else
    {
      $("#labelsContainer").html('<div style="font-size: 14px; font-weight: bold; color: red; padding: 10px;">' + "You did not provide a table or one or more column elements in your code." + '</div>');
    }
  }
  else
  {
    var serializer = new XMLSerializer();
    var em = serializer.serializeToString(parserError);
    $("#labelsContainer").html('<div style="font-size: 14px; font-weight: bold; color: red; padding: 10px;">' + "Your XML code (entered in the Code Editor) is invalid. Check the Messages for more information and fix your XML code in the Code Editor." + '</div>');
    em = '<div style="font-size: 14px; font-weight: bold; color: red;">The DOMParser returned the following error messages:</div>' + em;
    showMessages(em);
  }
}

function setLabels()
{
  var xmlData = editor.getValue();
  var parser = new DOMParser();
  var doc = parser.parseFromString(xmlData, "application/xml");
  var parserError = getParserError(doc.documentElement);
  if(parserError==null)
  {
    clearMessages();

    var c = null;
    var columns = getTableColumns(doc);
    if(columns!=null && columns.length>0)
    {
      var len = columns.length;
      for(var i=0; i<len; i++)
      {
        c = columns[i];
        var input = document.getElementById("columnlabel_" + String(i));
        var v = input.value!=null ? input.value : "";
        c.label = v;
      }
      
      var root = doc.documentElement;
      var tableElements = root.getElementsByTagName("table");
      var tableElement = tableElements[0];
      var columnElements = tableElement.getElementsByTagName("column");
      var len = columnElements.length;
      for(var i=0; i<len; i++)
      {
        var columnElement = columnElements[i];
        c = columns[i];
        columnElement.setAttribute("label", c.label);
      }
      
      var serializer = new XMLSerializer();
      var newXMLData = serializer.serializeToString(doc);
      if(newXMLData.indexOf("<" + "?" + "xml")==-1)
      {
        newXMLData = '<' + '?' + 'xml version="1.0" encoding="UTF-8"' + '?' + '>' + "\n" + newXMLData;
      }
      else
      {
        var firstGTIndex = newXMLData.indexOf(">");
        if(newXMLData.charAt(firstGTIndex + 1)=="<")
        {
          newXMLData = newXMLData.substring(0, firstGTIndex + 1) + "\n" + newXMLData.substring(firstGTIndex + 1);
        }
      }
      editor.setValue(newXMLData);
    }
  }
}

