function getElementPosition(e)
{
  var rect = e.getBoundingClientRect();
  var doc = document;
  var docElement = doc.documentElement;
  var body = doc.body;
  var scrollTop = (window.pageYOffset != null ? window.pageYOffset : (docElement.scrollTop != null ? docElement.scrollTop : body.scrollTop));
  var scrollLeft = (window.pageXOffset != null ? window.pageXOffset : (docElement.scrollLeft != null ? docElement.scrollLeft : body.scrollLeft));
  var clientTop = (docElement.clientTop != null ? docElement.clientTop : (body.clientTop != null ? body.clientTop : 0));
  var clientLeft = (docElement.clientLeft != null ? docElement.clientLeft : (body.clientLeft != null ? body.clientLeft : 0));
  var top = Math.round(rect.top + scrollTop - clientTop);
  var left = Math.round(rect.left + scrollLeft - clientLeft);
  var zIndex = parseFloat(e.style.zIndex);
  return {x: left, y: top, zIndex: Math.floor(zIndex)};
}

