function getNumberStylePropertyValue(element, property)
{
  var pv = element.style[property];
  if (pv == null || pv == undefined || pv == "")
  {
    var cStyle = window.getComputedStyle(element);
    pv = cStyle[property];
  }
  var nv = parseFloat(pv);
  if (isNaN(nv))
  {
    nv = 0;
  }
  return nv;
}

