  formData.append("targetfolder", targetFolder);
  $.ajax({
    url: "m13e.php",
    data: formData,
    cache: false,
    contentType: false,
    processData: false,
    method: 'POST',
    type: 'POST', // For jQuery before 1.9
    async: true,
    cache: false,
            headers: {"cache-control": "no-cache"},
    success: function (_data, _textStatus, _jqXHR)
    {
      $('#wait').fadeOut(400, function () {
        $('#wait').css('display', 'none');
      });
      if (_data == "OK")
      {
        $("#firstTimeMessage").css('display', 'none');
        $('#settings').fadeOut().css('display', 'none');
      } else
      {
        showSettingsMessages(_data);
      }
    },
    error: function (_jqXHR, _textStatus, _errorThrown)
    {
      $('#wait').fadeOut(400, function () {
        $('#wait').css('display', 'none');
      });
      showSettingsMessages("Communication error: " + _textStatus);
    }
  });
}

