}

function setTableDependencies($tableName, &$schema, &$dependencies)
{
  $t = $schema->getTable($tableName);
  $fks = $t->getForeignKeys();
  if(isset($fks))
  {
    $c = count($fks);
    for($i=0; $i<$c; $i++)
    {
      $fk = $fks[$i];
      $rt = $fk->getReferencedTable();
      if($rt!=$tableName)
      {
        if(!in_array($rt, $dependencies))
        {
          $dependencies[] = $rt;
          setTableDependencies($rt, $schema, $dependencies);
        }
      }
    }
  }
}

function getLocale()
{
  $serverName = isset($_SERVER) && isset($_SERVER['SERVER_NAME']) ? $_SERVER['SERVER_NAME'] : null;
  return isset($serverName) && strlen($serverName)>2 && substr($serverName, strlen($serverName) - 2) == "hu" ? "hu_HU" : "en_US";
}

function &generateXMLDefinition($i18n)
{
  $xml = null;
  $locale = getLocale();
  $dbms = new \com\cc\odbd\ret\dbms\MySQL();
  $dbms->connect($_SESSION["mydbdiffo_hostname"], 
                 $_SESSION["mydbdiffo_port"], 
                 $_SESSION["mydbdiffo_database"], 
                 $_SESSION["mydbdiffo_user"], 
                 $_SESSION["mydbdiffo_password"], 
                 $_SESSION["mydbdiffo_charset"]);
  $catalogFilter = array("def");
  $schemaFilter = array();
  $fv = array("catalog" => "def", "schema" => $_SESSION["mydbdiffo_database"]);
  $schemaFilter[] = $fv;
  $model = $dbms->getExportedModel($catalogFilter, $schemaFilter, null);
  $dbms->disconnect();
  $catalogs = $model->getCatalogs();
  if(isset($catalogs) && count($catalogs)>0)
  {
    $catalog = $catalogs[0];
    $schemas = $catalog->getSchemas();
    if(isset($schemas) && count($schemas)>0)
    {
      $schema = $schemas[0];
      $tables = $schema->getTables();
      if(isset($tables) && count($tables)>0)
      {
        $tableName = $_REQUEST["tablename"];
        $table = $schema->getTable($tableName);
        $singleIntegerColumn = false;
        $ai = false;
        $pk4Check = $table->getPrimaryKey();
        if(isset($pk4Check))
        {
          $pkcs = $pk4Check->getColumns();
          $pkcsc = count($pkcs);
          if($pkcsc==1)
          {
            $pkColumnName = $pkcs[0];
            $pkColumn = $table->getColumn($pkColumnName);
            $pkType = $pkColumn->getType();
            $ai = $pkColumn->isAutoIncrement();
            $singleIntegerColumn = $dbms->isIntegerType($pkType);
          }
        }
        
        $eColumns = $table->getColumns();
        $cEC = count($eColumns);
        
        if($singleIntegerColumn && $cEC>1 && $ai)
        {
          $columns = "";
          $controls = "";
          $optionsDefinitions = array();
          $tableDependencies = array();
          $optionsList = array();

          for($iEC=0; $iEC<$cEC; $iEC++)
          {
            $c = $eColumns[$iEC];
            $options = "";
            $inputField = "textInput";
            $mapping = "";
            $width = "250";
            $height = "";
            $renderer = "label";
            $cName = $c->getName();
            $cType = $c->getType();
            $cFullType = $c->getFullType();
            $cLength = $c->getLength();
            $cNull = $c->isNullable() ? "true" : "false";
            $required = $c->isNullable() ? "false" : "true";
            $cPK = $table->isPrimaryKeyColumn($cName);
            $cFK = $table->getForeignKeyByColumn($cName);
            if ($cPK)
            {
              $editable = "false";
              $key = "true";
            }
            else
            {
              $editable = "true";
              $key = "false";
            }

            $validator = null;

            if($editable == "true")
            {
              if(isset($cFK))
              {
                if (!$c->isNullable())
                {
                  $validator = '<validator type="minimal" required="true" />';
                }
              }
              else
              if ($dbms->isStringType($cType))
              {
                if ($dbms->hasLength($cType))
                {
                  $minLength = $c->isNullable() ? "0" : "1";
                  $columnLength = $c->getLength();
                  $maxLength = isset($columnLength) ? strval($columnLength) : '1';
                  $validator = '<validator type="string" minLength="'.$minLength.'" maxLength="'.$maxLength.'" required="'.$required.'" />';
                }
                else
                {
                  if (!$c->isNullable())
                  {
                    $validator = '<validator type="string" minLength="1" required="true" />';
                  }
                }
              }
              else
              if ($dbms->isNumericType($cType))
              {
                $precision = "";
                $scale = $c->getScale();
                if (isset($scale))
                {
                  $precision = 'precision="'.strval($scale).'" ';
                }
                else
                if ($dbms->isIntegerType($cType))
                {
                  $precision = 'precision="0" ';
                }

                $minValue = $dbms->getNumericMinValueAsString($cType, $c->getPrecision(), $scale);
                $maxValue = $dbms->getNumericMaxValueAsString($cType, $c->getPrecision(), $scale);

                $validator = '<validator type="number" minValue="'.$minValue.'" maxValue="'.$maxValue.'" '.$precision.'required="'.$required.'" />';
              }
              else
              if ($dbms->isDateTimeType($cType))
              {
                $validator = '<validator type="dateTime" required="'.$required.'" />';
              }
              else
              if ($dbms->isDateType($cType))
              {
                $validator = '<validator type="date" required="'.$required.'" />';
              }
              else
              if ($dbms->isTimeType($cType))
              {
                $validator = '<validator type="time" required="'.$required.'" />';
              }
              else
              {
                if (!$c->isNullable())
                {
                  $validator = '<validator type="minimal" required="true" />';
                }
              }

              if(isset($validator))
              {
                $validator = "\r\n      ".$validator."\r\n";
              }
            }

            // If it is a foreign key we create an options element for it.
            if(isset($cFK))
            {
              $refs = $cFK->getReferences();
              $cRef = count($refs);
              if($cRef==1)
              {
                $ref = $refs[0];
                $referencedTableName = $cFK->getReferencedTable();
                $referencedColumnName = $ref->getReferencedColumn();
                $referencedTable = $schema->getTable($referencedTableName);
                $label = $referencedColumnName;

                // guessing label
                $cs = $referencedTable->getColumns();
                $cc = count($cs);
                $defaultLabelNames = array('name', 'label', 'nev', 'megnevezes', 'cimke');
                $dlnc = count($defaultLabelNames);
                for($i=0;$i<$cc;$i++)
                {
                  $c = $cs[$i];
                  $cn = $c->getName();
                  $lccn = strtolower($cn);
                  for($j=0;$j<$dlnc;$j++)
                  {
                    $dln = $defaultLabelNames[$j];
                    if($lccn==$dln || strpos($lccn, $dln)!==false)
                    {
                      $label = $cn;
                    }
                  }
                }

                $options = $referencedTableName;
                $inputField = "comboBox";
                if(!in_array($referencedTableName, $optionsList))
                {
                  $optionsDefinition = "  <options name=\"$referencedTableName\" value=\"$referencedColumnName\" label=\"$label\" sourceURL=\"$referencedTableName.php?action=SELECT\" orderBy=\"$label\" refreshedAfterModification=\"false\" maintenanceURL=\"$referencedTableName.php\"/>";
                  $optionsDefinitions[$referencedTableName] = $optionsDefinition;
                  $dependencies = array();
                  setTableDependencies($referencedTableName, $schema, $dependencies);
                  $tableDependencies[$referencedTableName] = $dependencies;
                  $optionsList[] = $referencedTableName;
                }
              }
            }

            if($cType=="tinytext" || $cType=="text" || $cType=="mediumtext" || $cType=="longtext")
            {
              $inputField = "textArea";
              $height = "250";            
            }
            else
            if(($cType=="varchar" || $cType=="char") && $cLength==1)
            {
              $inputField = "checkBox";
              $mapping = "Y,N";
              $width = "";
              $renderer = "checkBox";
              $validator = null;
            }
            else
            if($cType=="datetime" || $cType=="timestamp")
            {
              $inputField = "dateTimeField";
            }
            else
            if($cType=="time")
            {
              $inputField = "timeField";
            }
            else
            if($cType=="date")
            {
              $inputField = "dateField";
            }

            $columns .= "    <column name=\"$cName\" key=\"$key\" label=\"$cName\" type=\"$cFullType\" nullable=\"$cNull\" options=\"$options\" mapping=\"$mapping\" />\r\n";
            $controls .= "    <control name=\"$cName\" type=\"$inputField\" label=\"\" editable=\"$editable\" password=\"false\" visible=\"true\" width=\"$width\" height=\"$height\" mapping=\"\" options=\"\" url=\"\" target=\"_blank\" buttonLabel=\"\" comment=\"\" commentWidth=\"\" commentHeight=\"\" help=\"\">".$validator."    </control>\r\n";
            $datagrid .= "    <column name=\"$cName\" label=\"\" options=\"\" minWidth=\"100\" renderer=\"$renderer\" mapping=\"\"/>\r\n";
          }

          $compareFunction = function ($a, $b) use ($tableDependencies)
                              {
                                $r = 0;
                                $aDeps = $tableDependencies[$a];
                                $bDeps = $tableDependencies[$b];
                                if(in_array($a, $bDeps) && !in_array($b, $aDeps))
                                {
                                  $r = -1;
                                }
                                else
                                if(!in_array($a, $bDeps) && in_array($b, $aDeps))
                                {
                                  $r = +1;
                                }
                                else
                                {
                                  if($a==$b)
                                  {
                                    $r = 0;
                                  }
                                  else
                                  {
                                    $r = $a<$b ? -1 : +1;
                                  }
                                }
                                return $r;
                              };
          usort($optionsList, $compareFunction);
          $allODs = "";
          foreach($optionsList as $ops)
          {
            $od = $optionsDefinitions[$ops];
            $allODs .= $od."\r\n";
          }

          $s = "<?xml version=\"1.0\" encoding=\"UTF-8\"?>\r\n";

          $s .= "<definition windowTitle=\"$tableName\" layout=\"split\" colonUsedAtItemLabels=\"true\" language=\"".substr($locale, 0, 2)."\" country=\"".substr($locale, 3, 2)."\" heartbeatPeriod=\"300\" resourceBundle=\"\" databaseType=\"mysql\" frameworkEmbedded=\"false\">\r\n";
          $s .= $allODs . "\r\n";
          $s .= "  <table name=\"$tableName\">\r\n";
          $s .= $columns;
          $s .= "  </table>\r\n";
          $s .= "  <tab label=\"".$i18n[$locale]["form"]."\">\r\n";
          $s .= $controls;
          $s .= "  </tab>\r\n";
          $s .= "  <dataGrid headerHeight=\"26\" rowHeight=\"26\" filterRowHeight=\"26\" filterRowVisible=\"true\">\r\n";
          $s .= $datagrid;
          $s .= "  </dataGrid>\r\n";
          $s .= "</definition>\r\n";

          $xml = $s;
        }
      }
    }
  }
  return $xml;
}

