function appendToHTMLMenu($xml, &$htmlMenu)
{
  $label = isset($xml['label']) ? (string) $xml['label'] : null;
  if(!isset($label))
  {
    $label = "";
  }
  $target = isset($xml['target']) ? (string) $xml['target'] : null;
  if(!isset($target) || strlen($target)==0)
  {
    $target = "_self";
  }
  $url = isset($xml['url']) ? (string) $xml['url'] : null;
  if(!isset($url) || strlen($url)==0)
  {
    $url = "#";
  }
  $c = $xml->count();
  $htmlMenu .= "<li><a href=\"".$url."\" target=\"".$target."\">".$label.($c>0 ? "&nbsp;&#9660;" : "")."</a>";
  if($c>0)
  {
    $htmlMenu .= "\r\n<ul>\r\n";
    $cs = $xml->children();
    foreach($cs as $c)
    {
      appendToHTMLMenu($c, $htmlMenu);
    }
    $htmlMenu .= "</ul>\r\n";
  }
  $htmlMenu .= "</li>\r\n";
}
        
function &generateMenu($targetFolder, &$xml)
{
  $m13e_messages = null;
  if(!file_exists($targetFolder.DIRECTORY_SEPARATOR."m13e_header.php"))
  {
    if(file_put_contents($targetFolder.DIRECTORY_SEPARATOR."m13e_header.php", $GLOBALS["m13e_header"]) === false)
    {
      $m13e_messages = $targetFolder.DIRECTORY_SEPARATOR."m13e_header.php generation failed.";
    }
  }
  if(!isset($m13e_messages))
  {
    $m13eHeader = file_get_contents($targetFolder.DIRECTORY_SEPARATOR."m13e_header.php");
    if($m13eHeader!==false)
    {
      $beginText = "<!-- BEGIN OF MENU / DO NOT REMOVE IT! -->";
      $endText = "<!-- END OF MENU / DO NOT REMOVE IT! -->";
      $beginIndex = strpos($m13eHeader, $beginText);
      $endIndex = strpos($m13eHeader, $endText);
      if($beginIndex!==false && $endIndex!==false)
      {
        $menu = "";
        libxml_use_internal_errors(true);
        $menuXML = simplexml_load_string($xml);
        $em = null;
        if($menuXML!==FALSE)
        {
          $cs = $menuXML->children();
          foreach($cs as $c)
          {
            appendToHTMLMenu($c, $menu);
          }
        }
        else
        {
          include "xmlErrorCodes.php";
          $errors = libxml_get_errors();
          foreach ($errors as $error)
          {
            if(isset($em))
            {
              $em .= "<br/>";
            }
            else
            {
              $em = "";
            }
            $code = $error->code;
            $codeName = "";
            if(isset($code))
            {
              if(isset($xmlErrorCodes[$code]))
              {
                $codeName = $xmlErrorCodes[$code];
              }
            }
            else
            {
              $code = "";
            }
            $em .= "Code: ".$codeName." (".$code."), Line: ".strval($error->line).", Message: ".$error->message;
          }
          libxml_clear_errors();
        }
        if(!isset($em))
        {
          $newM13EHeader = substr($m13eHeader, 0, $beginIndex + strlen($beginText)) . "\r\n" .
                           $menu . "\r\n" .
                           substr($m13eHeader, $endIndex);
          if(file_put_contents($targetFolder.DIRECTORY_SEPARATOR."m13e_header.php", $newM13EHeader) === false)
          {
            $m13e_messages = $targetFolder.DIRECTORY_SEPARATOR."m13e_header.php save failed. (Menu generation)";
          }
        }
        else
        {
          $m13e_messages = $em;
        }
      }
    }
    else
    {
      $m13e_messages = "The program cannot read the file ".$targetFolder.DIRECTORY_SEPARATOR."m13e_header.php.";
    }
  }
  return $m13e_messages;
}

